<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class course_model extends CI_model{		function add_course_data($data)	{		$this->db->insert('edu_course',$data);		return $this->db->insert_id();	}				function get_course_detail()	{		 $this->db->select('*');        $this->db->from('edu_course');		$this->db->order_by('id','DESC');        $query = $this->db->get();        return $result = $query->result();			}		function get_course_list()	{		 $this->db->select('*');        $this->db->from('edu_course');		$this->db->where('status', 'Active');		$this->db->order_by('id','DESC');        $query = $this->db->get();        return $result = $query->result();			}		function get_course_data_by_id($id)	{		$this->db->select('*');		$this->db->where('id', $id);        $q = $this->db->get('edu_course');		  return $result = $q->row();	}			function update_course_data($data_update_insurance_partner,$id){				$this->db->where('id',$id);		$this->db->update('edu_course',$data_update_insurance_partner);				}			function delete_course_by_id($id,$status){				 $this->db->where('id', $id);		$this->db->update('edu_course',$status); 		return true;	}				function get_front_page_data($limit, $start)	{	/* 	$this->db->select('*');		$this->db->order_by('start_date','DESC');		$this->db->where('status', '1');        $this->db->from('ficci_event');        $query = $this->db->get();        return $result = $query->result(); */				//$this->db->limit($limit, $start);		$query = $this->db->get("edu_course",$limit, $start);		if ($query->num_rows() > 0) {        	return $query->result_array();      	}	  		return false;			}				function get_all_course()	{		 $this->db->select('*');		 $this->db->where('status', '1');        $this->db->from('edu_course');        $query = $this->db->get();        return $result = $query->result();			}								}?>